import "./index.css";
import React, { useEffect } from "react";

function About() {
  useEffect(() => {
    // Add the animation class when the component mounts
    const img = document.getElementById("animated-img");
    img.classList.add("animate-up");

    // Remove the animation class after the animation finishes
    setTimeout(() => {
      img.classList.remove("animate-up");
    }, 1000); // Adjust this value to match the duration of the animation
  }, []);
  return (
    <div className="flex justify-center items-center flex-col">
      <section className="flex flex-row justify-between ">
        <div className="">
          <img
            src="https://th.bing.com/th/id/OIP.O2OxURVtfNV0CgA3-WhtZgHaEK?w=328&h=187&c=7&r=0&o=5&dpr=1.5&pid=1.7"
            alt="Avatar"
            id="animated-img"
            className="w-50 h-50 object-cover m-6 aspect-w-1 aspect-h-1 border-t-4 rounded-lg border-orange-500"
          ></img>
        </div>
        <div className="">
          <h3 className="flex justify-center">HI, I'm</h3>
          <h1 className="font-bold">
            <span style={{ color: "#f9532d" }}>Naveen</span> Mandava
          </h1>
          <p>I'm Front-end developer</p>
          <p className="">
            Professional Front-end developer,Main goal is to help and satisfy
            <br />
            the local and global client by the web development solutions
          </p>
        </div>
      </section>
    </div>
  );
}

export default About;
